import React, { Component } from 'react'
import classNames from 'classnames'
import PropTypes from 'prop-types'
import { withStyles } from '@material-ui/core/styles'
import {
  Typography,
  Paper,
  Grid,
  TextField,
  MenuItem,
  Input,
  InputAdornment,
  Button
 } from '@material-ui/core'

 const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  margin: {
    margin: theme.spacing.unit,
  },
  withoutLabel: {
    marginTop: theme.spacing.unit * 3,
  },
  textField: {
    flexBasis: 200,
    width: '100%',
  },
  button: {
    height: '75%',
    width: '100%',
    margin: theme.spacing.unit,
  },
});

class BoxState extends Component {
  constructor() {
    super()
    this.state = {
      searchNumber : 0,
      searchType: 1,
      users: []
    }
    this.getInfoBox = this.getInfoBox.bind(this)
  }

  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  }

  getInfoBox = () => {
    let fetchConf = {
      method: 'GET',
      mode: 'cors'
    }
    const {searchType, searchNumber} = this.state

    let url = `http://localhost:5000/api/dbcsv?type=${searchType}&number=${searchNumber}`
    console.log(url)
    fetch(url)
      .then(function(response) {
        if(response.ok) {
          this.setState({users: response})
        }
      })
      .catch(function(err) {
        console.log('Error con fetch')
      })

  }

  render() {
    const { classes } = this.props
    const searchTypes = [
            {name: 'Colector', value: 1},
            {name: 'Caja', value: 2},
            {name: 'Usuario', value: 3},
            {name: 'Medidor', value: 4}
          ]
    return(
      <div className='content'>
        <Grid container spacing={24}>
          <Grid item xs={12}>
            <Typography variant="h4" component="h4" align='center'>
              Verificación de Cajas
            </Typography>
          </Grid>
          <Grid item xs={5}>
            <TextField
              select
              label="Buscar por:"
              className={classNames(classes.margin, classes.textField)}
              value={this.state.searchType}
              onChange={this.handleChange('searchType')}
              variant="outlined"
            >
              {searchTypes.map(option => (
                <MenuItem key={option.value} value={option.value}>
                  {option.name}
                </MenuItem>
              ))}
            </TextField>
          </Grid>
          <Grid item xs={5}>
            <TextField
              label="Código"
              className={classNames(classes.margin, classes.textField)}
              variant="outlined"
            />
          </Grid>
          <Grid item xs={2}>
            <Button
              variant="contained"
              color="primary"
              className={classes.button}
              onClick={this.getInfoBox}
              >
              BUSCAR
            </Button>
          </Grid>
        </Grid>
      </div>
    )
  }
}

BoxState.propTypes = {
  classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(BoxState)
